mapper.update:
update movies 
set movie_id=#{movie_id}, tmdb_title=#{tmdb_title}, korea_title=#{korea_title}, english_title=#{english_title}, thema=#{thema},
synopsis=#{synopsis}, movie_url=#{movie_url}, subtitle_url=#{subtitle_url},trailer_url=#{trailer_url},poster_url=#{poster_url},backdrop_url=#{backdrop_url},
movie_grade=#{movie_grade},runtime=#{runtime},release_date=#{release_date},update_time=sysdate(),admin_id=#{admin_id},movie_del_name=#{movie_del_name},sub_del_name=#{sub_del_name},synchro=#{synchro}
where movie_idx = #{movie_idx}

update movies 
set status=0, admin_id=#{admin_id}, delete_time=sysdate()
where movie_idx = #{movie_idx}

update movies 
set synchro=0
where movie_idx = #{movie_idx}
